INSERT INTO recipe_master ( recipe_id, recipe_release_date, recipe_name, recipe_caption, recipe_caroly, recipe_season, recipe_pasta, recipe_pasta_name, recipe_pasta_html_file, recipe_pasta_note, recipe_pasta_amount, recipe_source, sub_category_id_1, sub_category_id_2, sub_category_id_3, recipe_attributes, recipe_item_group_name1, recipe_item_group_name2, recipe_detail1, recipe_detail2, recipe_detail3, recipe_detail4, recipe_detail5, recipe_detail6, recipe_detail7, recipe_detail8, recipe_note1, recipe_note2 ) VALUES ('388', '2014/5/2', 'えんどう豆のスープパスタ', '旬のグリーンピースのやさしい味わいが、ひと口でふわりと広がります。', '237', '春', '0010000000', 'エルボマカロニ', 'elbo.html', '', '60g', '0001000000', '9901', '0', '0', '1100000000', '', '', 'グリーンピースは塩少々を加えた湯で3分ゆで、冷水にとる。玉ねぎは薄切りにする。', '鍋にオリーブ油大さじ1を熱し、玉ねぎを炒める。透き通ってきたら水を加えて煮立て、グリーンピース、塩少々を加えてひと煮立ちさせる。', '{#2}をミキサーでかくはんし、こし器でこす。塩少々で味を調え、冷やす。', 'マカロニは塩(分量外)を加えた湯でゆでる。ゆで上がったらざるに上げ、オリーブ油少々をまぶして冷ます。器に{#3}、マカロニを盛り、オリーブ油少々をふる。', '', '', '', '', '', 'ブイヨンなどを使わず、シンプルな味つけで豆のおいしさを十分に引き出したなめらかなスープに、プリッとしたショートパスタを浮き実がわりに加えます。朝食にもぴったり。');
INSERT INTO recipe_master ( recipe_id, recipe_release_date, recipe_name, recipe_caption, recipe_caroly, recipe_season, recipe_pasta, recipe_pasta_name, recipe_pasta_html_file, recipe_pasta_note, recipe_pasta_amount, recipe_source, sub_category_id_1, sub_category_id_2, sub_category_id_3, recipe_attributes, recipe_item_group_name1, recipe_item_group_name2, recipe_detail1, recipe_detail2, recipe_detail3, recipe_detail4, recipe_detail5, recipe_detail6, recipe_detail7, recipe_detail8, recipe_note1, recipe_note2 ) VALUES ('389', '2014/6/2', 'バター風味のトマトスパゲッティ', '仕上げにバターを加えて、コクのあるトマトソースに仕上げます。', '560', '夏', '1000000000', 'スパゲッティ', 'spaghetti.html', '', '200g', '0010000000', '303', '0', '0', '0100000000', '', '', '玉ねぎはみじん切りにする。にんにくは半分に切り、芽をとってつぶす。スパゲッティは塩(分量外)を加えた湯でゆでる。', '鍋にオリーブ油とにんにくを入れて熱し、香りが立ったら、玉ねぎを加えて炒める。しんなりしたらトマト水煮をつぶしながら加え、塩少々を加えて20分ほど煮る。', 'ゆで上がったスパゲッティを加え、バター、スパゲッティのゆで汁適量、塩、こしょう各少々で味を調える。', '', '', '', '', '', '', 'いつものフレッシュ感あふれるトマトソースに比べ、バターのおかげでこっくりとまろやか。トマトの酸味も抑えられ、やわらかい味わいのトマトソースに。');
INSERT INTO recipe_master ( recipe_id, recipe_release_date, recipe_name, recipe_caption, recipe_caroly, recipe_season, recipe_pasta, recipe_pasta_name, recipe_pasta_html_file, recipe_pasta_note, recipe_pasta_amount, recipe_source, sub_category_id_1, sub_category_id_2, sub_category_id_3, recipe_attributes, recipe_item_group_name1, recipe_item_group_name2, recipe_detail1, recipe_detail2, recipe_detail3, recipe_detail4, recipe_detail5, recipe_detail6, recipe_detail7, recipe_detail8, recipe_note1, recipe_note2 ) VALUES ('390', '2014/7/1', 'カポナータのペンネ', 'シチリアの伝統料理、カポナータをパスタに。冷製にしてもおいしい。', '484', '夏', '0100000000', 'ペンネ', 'penne.html', '', '120g', '0100000000', '299', '0', '0', '0101000000', '', '', 'なすは小さめの乱切りにする。プチトマトは半分に切る。玉ねぎは薄切り、にんにくは半分に切って芽をとってつぶす。', 'フライパンにオリーブ油を1cmほどの深さに入れて熱し、なすを薄く色づくまで揚げ焼きにし、油をきる。', '別のフライパンにオリーブ油大さじ1とにんにくを入れて熱し、色づいてきたら玉ねぎを加えて炒める。薄く色づいてきたらプチトマト、赤ワインビネガー、塩少々を加え、トマトが煮くずれるまで煮る。△魏辰┐討劼伴僂垢襦', 'ゆで上がったペンネを加え、ペンネのゆで汁適量、塩、こしょう各少々で味を調える。', '', '', '', '', '', '赤ワインビネガーを加えたら、しっかり煮つめるのがコツ。酸味がコクとうまみに変化します。とろりとしたなすとトマトをたっぷりとパスタにからめてどうぞ。');
INSERT INTO recipe_master ( recipe_id, recipe_release_date, recipe_name, recipe_caption, recipe_caroly, recipe_season, recipe_pasta, recipe_pasta_name, recipe_pasta_html_file, recipe_pasta_note, recipe_pasta_amount, recipe_source, sub_category_id_1, sub_category_id_2, sub_category_id_3, recipe_attributes, recipe_item_group_name1, recipe_item_group_name2, recipe_detail1, recipe_detail2, recipe_detail3, recipe_detail4, recipe_detail5, recipe_detail6, recipe_detail7, recipe_detail8, recipe_note1, recipe_note2 ) VALUES ('391', '2014/8/1', 'ピーマンとアンチョビのスパゲッティ', '蒸し煮にしてくたくたになったピーマンを合わせた、大人味スパゲッティ。', '601', '夏', '1000000000', 'スパゲッティ', 'spaghetti.html', '', '200g', '1000000000', '199', '0', '0', '0110000000', '', '', 'ピーマンは縦1cm幅に切る。アンチョビは粗く刻む。にんにくは半分に切り、芽をとってつぶす。スパゲッティは塩(分量外)を加えた湯でゆでる。', '鍋に水大さじ2、オリーブ油大さじ1、ピーマンを入れてふたをして火にかけ、3分蒸し煮にする。ふたをはずしてアンチョビを加え混ぜる。', 'ゆで上がったスパゲッティを加え、スパゲッティのゆで汁適量、塩、こしょうで味を調える。', '', '', '', '', '', '', 'ピーマンは蒸し煮にして、やわらかく、甘みを引き出します。1人分3個使っても、ペロリと食べてしまう食べやすさ。うまみと塩けの調味料、アンチョビとも好相性です。');
INSERT INTO recipe_master ( recipe_id, recipe_release_date, recipe_name, recipe_caption, recipe_caroly, recipe_season, recipe_pasta, recipe_pasta_name, recipe_pasta_html_file, recipe_pasta_note, recipe_pasta_amount, recipe_source, sub_category_id_1, sub_category_id_2, sub_category_id_3, recipe_attributes, recipe_item_group_name1, recipe_item_group_name2, recipe_detail1, recipe_detail2, recipe_detail3, recipe_detail4, recipe_detail5, recipe_detail6, recipe_detail7, recipe_detail8, recipe_note1, recipe_note2 ) VALUES ('392', '2014/9/1', 'セミドライトマトのスパゲッティ', 'うまみと甘みが凝縮したプチトマトの、濃厚なソースでいただきます。', '580', '秋', '1000000000', 'スパゲッティ', 'spaghetti.html', '', '200g', '0100000000', '299', '0', '0', '0101000000', '', '', 'プチトマトは半分に切って天板に並べ、140度のオーブンで40分焼く。にんにくは半分に切り、芽をとってつぶす。スパゲッティは塩(分量外)を加えた湯でゆでる。', 'フライパンにオリーブ油、にんにく、赤唐辛子を入れて熱し、香りが立ったら、プチトマト、スパゲッティのゆで汁1/3カップを加え、1分ほど煮る。', 'ゆで上がったスパゲッティを加え、塩、こしょうで味を調える。器に盛り、ちぎったバジルを散らす。', '', '', '', '', '', '', '低温のオーブンでじっくりと焼いたセミドライトマトは、ミキサーにかけてペーストにしてもパスタによく合います。たっぷり作っておつまみにもどうぞ。');
INSERT INTO recipe_master ( recipe_id, recipe_release_date, recipe_name, recipe_caption, recipe_caroly, recipe_season, recipe_pasta, recipe_pasta_name, recipe_pasta_html_file, recipe_pasta_note, recipe_pasta_amount, recipe_source, sub_category_id_1, sub_category_id_2, sub_category_id_3, recipe_attributes, recipe_item_group_name1, recipe_item_group_name2, recipe_detail1, recipe_detail2, recipe_detail3, recipe_detail4, recipe_detail5, recipe_detail6, recipe_detail7, recipe_detail8, recipe_note1, recipe_note2 ) VALUES ('393', '2014/10/1', 'きのこのサラダパスタ', 'きのこはじっくりと炒めて、食感よく仕上げます。サラダ感覚のひと皿。', '398', '秋', '0001000000', 'ファルファッレ', 'fal.html', '', '100g', '0000010000', '9904', '0', '0', '1101000000', '', '', 'ファルファッレは塩(分量外)を加えた湯でゆでてざるに上げ、オリーブ油少々(分量外)をまぶす。', '生ハム、ルッコラは食べやすく切る。しいたけは1cm厚さ、エリンギは乱切り、しめじは小房に分ける。', 'フライパンにオリーブ油大さじ1を熱してきのこを入れ、塩少々をふって炒める。8割ほど火が通ったら、バルサミコ酢を加えてひと炒めする。', 'ボウルに{#1}、生ハム、ルッコラを合わせ、残りのオリーブ油、ワインビネガー、塩、こしょう各少々を加えてあえる。器に盛り、粗びき黒こしょうをふる。', '', '', '', '', '', 'ルッコラはサラダほうれん草やトレビス、べビーリーフなど好みの野菜に変えてもおいしく作れます。ほどよい酸味でさっぱり。ワインのおともにもおすすめです。');
INSERT INTO recipe_master ( recipe_id, recipe_release_date, recipe_name, recipe_caption, recipe_caroly, recipe_season, recipe_pasta, recipe_pasta_name, recipe_pasta_html_file, recipe_pasta_note, recipe_pasta_amount, recipe_source, sub_category_id_1, sub_category_id_2, sub_category_id_3, recipe_attributes, recipe_item_group_name1, recipe_item_group_name2, recipe_detail1, recipe_detail2, recipe_detail3, recipe_detail4, recipe_detail5, recipe_detail6, recipe_detail7, recipe_detail8, recipe_note1, recipe_note2 ) VALUES ('394', '2014/11/4', 'しゃきしゃきじゃがいものたらこスパゲッティ', 'さっとゆでたじゃがいもの軽い食感が楽しい。たらことも好相性です。', '548', '秋', '1000000000', 'スパゲッティ', 'spaghetti.html', '', '180g', '0000000001', '9999', '0', '0', '0110000000', '', '', 'たらこは薄皮を除いてボウルに入れ、オリーブ油、バター、レモン汁、塩少々を加えて混ぜる。じゃがいもはスライサーで細切りにし、水にさらす。', 'スパゲッティは塩(分量外)を加えた湯でゆでる。ゆで上がり1分前にじゃがいもを加えて一緒にゆでる。', 'ゆで上がった{#2}を{#1}のボウルに加え混ぜ、塩、こしょう各少々で味を調える。器に盛り、根元を落としたスプラウトを散らす。', '', '', '', '', '', '', 'いつものたらこスパゲッティも目先が変わって楽しい！じゃがいもはメークインがおすすめ。スライサーを使えば、細切りもあっという間です。たらこソースはレモン汁を効かせて、さっぱりと。');
INSERT INTO recipe_master ( recipe_id, recipe_release_date, recipe_name, recipe_caption, recipe_caroly, recipe_season, recipe_pasta, recipe_pasta_name, recipe_pasta_html_file, recipe_pasta_note, recipe_pasta_amount, recipe_source, sub_category_id_1, sub_category_id_2, sub_category_id_3, recipe_attributes, recipe_item_group_name1, recipe_item_group_name2, recipe_detail1, recipe_detail2, recipe_detail3, recipe_detail4, recipe_detail5, recipe_detail6, recipe_detail7, recipe_detail8, recipe_note1, recipe_note2 ) VALUES ('395', '2014/12/1', 'れんこん入りミートソーススパゲッティ', '定番ミートソースにれんこんの食感を加えて、食べごたえをアップ。', '786', '冬', '1000000000', 'スパゲッティ', 'spaghetti.html', '', '200g', '0100000000', '201', '0', '0', '0001000000', 'A', '', 'れんこんは皮つきのまま5mm角、玉ねぎ、セロリ、にんじん、にんにくはみじん切りにする。', '鍋にオリーブ油とにんにくを入れて熱し、にんにくが色づいてきたら、玉ねぎ、セロリ、にんじんを入れて色づくまで炒める。れんこんも加えてさらに炒める。', 'フライパンにオリーブ油少々(分量外)を熱し、ひき肉をかたまりのまま入れ、表面に焼き色がつくように強火で焼く。焼き色がついたら、{#2}に加えてざっとほぐす。', '赤ワインを加えて半量ほどになるまで煮詰め、トマト水煮をつぶしながら加え、Aを加えて弱火で30分煮る。スパゲッティは塩(分量外)を加えた湯でゆでる。', '{#4}に、ゆで上がったスパゲッティを加え、スパゲッティのゆで汁適量、塩、こしょう、チーズの半量、バターを加えて混ぜる。器に盛り、残りのチーズをかける。', '', '', '', '', 'れんこんは皮つきのまま加えて、風味をアップ。ミートソースはれんこんの代わりにごぼうを使ったり、刻んだ干ししいたけを加えたりしても、おいしく作れます。');
INSERT INTO recipe_master ( recipe_id, recipe_release_date, recipe_name, recipe_caption, recipe_caroly, recipe_season, recipe_pasta, recipe_pasta_name, recipe_pasta_html_file, recipe_pasta_note, recipe_pasta_amount, recipe_source, sub_category_id_1, sub_category_id_2, sub_category_id_3, recipe_attributes, recipe_item_group_name1, recipe_item_group_name2, recipe_detail1, recipe_detail2, recipe_detail3, recipe_detail4, recipe_detail5, recipe_detail6, recipe_detail7, recipe_detail8, recipe_note1, recipe_note2 ) VALUES ('396', '2015/1/5', 'いんげん豆とからすみのパスタ', 'ゆでたてのいんげん豆と、ほどよい塩けとうまみのからすみを使った贅沢な味わい。', '478', '冬', '0010000000', 'カールマカロニ', 'curl.html', 'フジッリ', '120g', '0000100000', '499', '0', '0', '0001000000', 'A', '', '白いんげん豆は水にひと晩つけておく。にんにくは半分に切り、芽を取ってつぶす。', '鍋に白いんげん豆とA、塩少々を入れて火にかけ、煮立ったらアクをとり、弱火で45?70分煮る。豆がやわらかくなったら、香味野菜を取り除き、ペンネを加えて一緒にゆでる。', 'フライパンにオリーブ油、にんにくを入れて熱し、香りが立ったらゆで上がった{#2}、ゆで汁1/3カップを加え、オリーブ油、からすみを加えて混ぜる。器に盛り、からすみ少々(分量外)をふる。', '', '', '', '', '', '', 'ゆでいんげん豆の滋味あふれるおいしさは格別。人気のからすみを合わせて、寒い季節にほっこりとするひと皿に仕上げます。豆のゆで時間は季節や種類によっても変わってくるので、様子を見ながら加減して下さい。');
INSERT INTO recipe_master ( recipe_id, recipe_release_date, recipe_name, recipe_caption, recipe_caroly, recipe_season, recipe_pasta, recipe_pasta_name, recipe_pasta_html_file, recipe_pasta_note, recipe_pasta_amount, recipe_source, sub_category_id_1, sub_category_id_2, sub_category_id_3, recipe_attributes, recipe_item_group_name1, recipe_item_group_name2, recipe_detail1, recipe_detail2, recipe_detail3, recipe_detail4, recipe_detail5, recipe_detail6, recipe_detail7, recipe_detail8, recipe_note1, recipe_note2 ) VALUES ('397', '2015/2/2', 'くたくたに煮た芽キャベツのスパゲッティ', 'くずれるくらいにやわらかく煮た芽キャベツを軽くつぶしてソースに。', '437', '冬', '1000000000', 'スパゲッティ', 'spaghetti.html', '', '200g', '1000000000', '199', '0', '0', '1101000000', 'A', '', '芽キャベツは半分に切る。にんにくは半分に切り、芽を取ってつぶす。アンチョビは粗く刻む。', '鍋にオリーブ油、にんにくを入れて熱し、香りが立ったら芽キャベツ、A、塩少々を加えてふたをし、やわらかくなるまで弱火で15分ほど蒸し煮にする。スパゲッティは塩(分量外)を加えた湯でゆでる。', '{#2}の鍋にアンチョビを加えて、木べらなどで芽キャベツをつぶすようにしながら炒める。スパゲッティのゆで汁適量を加えてよく混ぜ、ゆで上がったスパゲッティを加え、塩、こしょうで味を調える。', '', '', '', '', '', '', 'ミニサイズのキャベツのようですが、これが大きくなってキャベツになるわけではありません。普通のキャベツよりもビタミンCが豊富な優秀野菜。ゆっくり蒸し煮にして、甘みを引き出しましょう。');
INSERT INTO recipe_master ( recipe_id, recipe_release_date, recipe_name, recipe_caption, recipe_caroly, recipe_season, recipe_pasta, recipe_pasta_name, recipe_pasta_html_file, recipe_pasta_note, recipe_pasta_amount, recipe_source, sub_category_id_1, sub_category_id_2, sub_category_id_3, recipe_attributes, recipe_item_group_name1, recipe_item_group_name2, recipe_detail1, recipe_detail2, recipe_detail3, recipe_detail4, recipe_detail5, recipe_detail6, recipe_detail7, recipe_detail8, recipe_note1, recipe_note2 ) VALUES ('398', '2015/3/2', '新玉ねぎと塩漬け豚のスパゲッティ', 'うまみが凝縮した自家製塩豚をみずみずしい新玉ねぎと合わせて', '692', '春', '1000000000', 'スパゲッティ', 'spaghetti.html', '', '200g', '1000000000', '199', '0', '0', '0100000000', '', '', '豚肉に塩小さじ1/4をふってひと晩おき、7mmほどの厚さに切る。玉ねぎは1cm幅のくし形切りにする。', '鍋にオリーブ油を熱し、玉ねぎ、水大さじ1、塩少々を加えてふたをし、ときどき混ぜながら15分蒸し煮する。スパゲッティは塩(分量外)を加えた湯でゆでる。', 'フライパンにオリーブ油大さじ1を熱し、{#1}の豚肉をこんがりするまで炒める。玉ねぎを加えて炒め合わせ、ゆで上がったスパゲッティを加え混ぜ、ゆで汁適量、塩少々で味を調える。器に盛り、粗びき黒こしょう、パルメザンチーズ各適量をふる。', '', '', '', '', '', '', '塩漬け豚は大きなかたまりで作ってもOK。日持ちするので、炒めものなどにも利用できます。新玉ねぎは弱火でじっくり蒸し煮にして、そのやさしい甘み、風味を生かします。');
